mapper.update:
		update member
		set
			memberName = #{memberName}
            ,memberId = #{memberId}
            ,memberPwd = #{memberPwd}
         	,memberEmail = #{memberEmail}
         	,genderCd = #{genderCd}
         	,birthDay = #{birthDay}
         	,phoneNumber = #{phoneNumber}
    		where 1=1
    		and seq = #{seq}

mapper.delete:
delete from member
where 1 = 1
	and seq = #{seq}
